apiAnonymous:
when HTTP_REQUEST { 
    if { [HTTP::uri] starts_with "/SSO/" } { 
  	    pool sso-mod.markitqa.com_80.pool
        set NewUri [findstr [HTTP::uri] /SSO 4]
        HTTP::uri $NewUri
        HTTP::header replace Host sso-mod.markitqa.com
    }
    else { pool qa.services.mdgapp.net_80.pool }

}
