apiAnonymous:
when CLIENT_ACCEPTED {
   if { !([matchclass [IP::client_addr] equals Pimco_UAT]) } {
        # log local0. "[IP::client_addr]:[TCP::client_port]: does not match Pimco_UAT IPs allowed"
        reject
     }
} 

when HTTP_REQUEST {
    if { [matchclass [IP::client_addr] equals PIMCO_UAT_APM_DISABLE]  or [HTTP::uri] contains "/api" } { 
            ACCESS::disable
            # log local0. "Client [IP::client_addr]: APM DISABLED"
         }
         else {
            ACCESS::enable
            # log local0. "Client [IP::client_addr]: is being checked by APM policy"
         }
}
