      HTTP::header insert x-forwarded-for [getfield [IP::remote_addr] "%" 1 ]     

      if { [HTTP::uri] starts_with "https://onlineservices-ubs.markitqa.com/" } 
      { HTTP::uri [string map {https://onlineservices-ubs.markitqa.com/ /} [HTTP::uri]]  
  }  
