apiAnonymous:
when HTTP_REQUEST {
    switch -glob [string tolower [HTTP::uri]] {
                "/research-api*" {
                                pool research-ally.markitqa.com-research-api.com_80.pool
                                SSL::disable serverside
                                set uri [HTTP::uri [string map -nocase {"/research-api" "/apiman-gateway/AllyBank/ally-api"} [HTTP::uri]]]
                             }
                default { 
                          SSL::disable serverside
                          pool qa.services.mdgapp.net_80.pool
                        }
     }
}
