apiAnonymous:
when HTTP_REQUEST {
     switch -glob [string tolower [HTTP::uri]] {
           "/documents*" {
                set new_uri [string map { "/documents" "/" } [HTTP::uri]]
                HTTP::uri $new_uri
                HTTP::header replace Host uat.pimcodocs.markitqa.com 
                pool uat.pimcodocs.markitqa.com_443.pool
           }
     }
}
