apiAnonymous:
when CLIENTSSL_HANDSHAKE {
    set vipname [virtual name]
    set remote [IP::remote_addr]
    set suite [SSL::cipher name]

    if { ([matchclass [SSL::cipher name] equals Weak_ciphers_list]) } {
          log local0. "Virtual Server $vipname  Client IP is $remote  Selected cipher suite: $suite"
    }
}
