apiAnonymous:
when HTTP_REQUEST {

  if { !([HTTP::header exists "REQUEST-ID"]) } {
       set rid1 [call generate_REQUEST-ID::rlg 8]
       set rid2 [call generate_REQUEST-ID::rlg 4]
       set rid3 [call generate_REQUEST-ID::rlg 4]
       set rid4 [call generate_REQUEST-ID::rlg 4]
       set rid5 [call generate_REQUEST-ID::rlg 12]
       set NewRID $rid1-$rid2-$rid3-$rid4-$rid5

       HTTP::header insert REQUEST-ID $NewRID

     #  log local0. "Random REQUEST-ID: $NewRID"
  }
}
