apiAnonymous:
when HTTP_REQUEST {
 STREAM::disable    
 if { not([HTTP::uri] starts_with "/apiman-gateway/MOD") } {
        HTTP::redirect https://reuters-api.markitqa.com/apiman-gateway/MOD[HTTP::uri]
# log local0. "[HTTP::uri]"
    }
}

when HTTP_RESPONSE {
   # Check if response type is text
   if {[HTTP::header value Content-Type] contains "text"}{
      STREAM::expression {@/apiman-gateway/MOD@@}
      # Enable the stream filter for this response only
      STREAM::enable
   }
}
